export function intersection(a, b) {
  return a.filter(v => b.includes(v));
}
export function dedupe(a, key) {
  if (!a || !Array.isArray(a) || !key) return [];
  const freqMap = {};
  a.forEach(x => freqMap[x[key]] = x);
  return Object.values(freqMap);
}
export function emptyArray(array) {
  array.splice(0, array.length);
  return array;
}
export function replaceItems(array, newItems) {
  emptyArray(array).push(...newItems);
  return array;
}
